#include <stdint.h>
#include <stdio.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

#define IRQNO_TIMER2 28
#define IRQNO_I2C1 31

uint32_t *pNVIC_IPRBase = (uint32_t *) 0xE000E400; //interuupt priority reg
uint32_t *pNVIC_ISERBase = (uint32_t *) 0xE000E100; // interuupt set enable reg
uint32_t *pNVIC_ISPRBase = (uint32_t *)0xE000E200; //interuupt set pending reg

void configure_priority_for_irqs(uint8_t irq_no , uint8_t priority_value)
{
	//1. find out IPRx
	uint8_t iprx = irq_no/4; //if irq is 5 so IPR1 will be used
	uint32_t *ipr = pNVIC_IPRBase+iprx;

	//2. position in IPRx
	uint8_t pos = (irq_no % 4) * 8;

	//3. configure the priority
	*ipr &= ~(0xFF << pos); // clear
	*ipr |= (priority_value << pos);

}

int main(void)
{
    //1- configure the priority of the peripherals
	configure_priority_for_irqs(IRQNO_TIMER2 , 0x80);
	configure_priority_for_irqs(IRQNO_I2C1 , 0x70);// higher priority than timer2

	//2- set the interrupt pending bit in the NVIC PR

	*pNVIC_ISPRBase |= (1 << IRQNO_TIMER2); // for only TIMER2

	//3- Enable the IRQs in NVIC ISER
	*pNVIC_ISERBase |= (1 << IRQNO_I2C1);
	*pNVIC_ISERBase |= (1 << IRQNO_TIMER2);

}

void TIM2_IRQHandler(void)
{
	printf("[TIM2_IRQHandler]\n");
	//setting the interrupt pending bit for I2C1
	*pNVIC_ISPRBase |= (1 << IRQNO_I2C1); // will halt timer2 interrupt and go
											//to i2c interrupt (higher priority)
	while(1);
}

void I2C1_EV_IRQHandler(void)
{
	printf("[I2C1_EV_IRQHandler]\n");
}








